mapper.delete:
delete from t_restaurant
where i_rest = #{i_rest}
AND i_user = #{i_user}

delete A from t_restaurant_menu A
inner join t_restaurant B
on
a.i_rest = b.i_rest
where b.i_rest = #{i_rest}
AND b.i_user = #{i_user}

delete A from t_restaurant_recommend_menu A
inner join
t_restaurant B
on a.i_rest = b.i_rest
where b.i_rest = #{i_rest}
AND b.i_user = #{i_user}

mapper.select:
SELECT i_rest,seq, menu_nm, menu_price, menu_pic 
FROM t_restaurant_recommend_menu 
where i_rest = #{i_rest}
